import React, { useEffect} from 'react';
import './App.scss';
import { connect } from 'react-redux';
import { Route, Routes } from 'react-router-dom';
import MediaQuery from 'react-responsive';
import Home from './components/Home.js';
import Login from './components/Login.js';
import { getCurrentUser } from './actions/currentUser.js';
import { fetchRuns } from './actions/fetchRuns';


function App(props) {

  useEffect(() => {
    props.getCurrentUser();
    props.fetchRuns();
  }, [props]);

  return (
    <div className="App">
      {/* <MediaQuery minWidth={800}> */}
      <Routes>
      <Route exact path="/login" component={Login} />
      {/* <Route exact path="/profile" render={() => loggedIn ? <RunApplicationsContainer /> : < Home />} />   */}
      </Routes>
      {/* </MediaQuery> */}
    </div>
  );
}
const mapStateToProps = state => {
  return ({
    loggedIn: !!state.currentUserReducer,
    runs: state.runsReducer
  })
}


export default connect(mapStateToProps, { getCurrentUser, fetchRuns }) (App);
